import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAlgorand } from '../contexts/AlgorandContext';

const CourseDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const { account } = useAlgorand();
  const [activeTab, setActiveTab] = useState('overview');

  // Mock data - replace with actual data from your backend
  const course = {
    id: Number(id),
    title: 'Blockchain Development Fundamentals',
    instructor: 'John Doe',
    rating: 4.5,
    students: 1234,
    price: 99.99,
    description: 'Learn the fundamentals of blockchain technology and development. This comprehensive course covers everything from basic concepts to advanced implementation.',
    modules: [
      {
        id: 1,
        title: 'Introduction to Blockchain',
        duration: '2 hours',
        lessons: [
          { id: 1, title: 'What is Blockchain?', duration: '30 mins' },
          { id: 2, title: 'How Blockchain Works', duration: '45 mins' },
          { id: 3, title: 'Types of Blockchains', duration: '45 mins' }
        ]
      },
      {
        id: 2,
        title: 'Smart Contracts',
        duration: '3 hours',
        lessons: [
          { id: 4, title: 'Introduction to Smart Contracts', duration: '45 mins' },
          { id: 5, title: 'Writing Your First Smart Contract', duration: '1 hour' },
          { id: 6, title: 'Testing Smart Contracts', duration: '1 hour 15 mins' }
        ]
      }
    ]
  };

  const handleEnroll = async () => {
    if (!account) {
      // Redirect to sign in
      return;
    }
    // Handle enrollment logic
  };

  return (
    <div className="section">
      <div className="container">
        {/* Course Header */}
        <div style={{ marginBottom: '3rem' }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>{course.title}</h1>
          <div style={{ display: 'flex', gap: '2rem', alignItems: 'center', marginBottom: '1rem' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <span style={{ color: '#f7c32e' }}>★</span>
              <span>{course.rating}</span>
              <span style={{ color: '#666' }}>({course.students} students)</span>
            </div>
            <div>Instructor: {course.instructor}</div>
          </div>
        </div>

        <div className="grid grid-2" style={{ gap: '3rem' }}>
          {/* Main Content */}
          <div>
            {/* Tabs */}
            <div style={{ 
              display: 'flex', 
              borderBottom: '1px solid var(--border-color)',
              marginBottom: '2rem'
            }}>
              {['overview', 'curriculum', 'reviews'].map(tab => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  style={{
                    padding: '1rem 2rem',
                    border: 'none',
                    background: 'none',
                    cursor: 'pointer',
                    borderBottom: activeTab === tab ? '2px solid var(--primary-color)' : 'none',
                    color: activeTab === tab ? 'var(--primary-color)' : 'var(--text-color)'
                  }}
                >
                  {tab.charAt(0).toUpperCase() + tab.slice(1)}
                </button>
              ))}
            </div>

            {/* Tab Content */}
            {activeTab === 'overview' && (
              <div>
                <h2 style={{ marginBottom: '1rem' }}>About This Course</h2>
                <p style={{ lineHeight: '1.6', color: '#666' }}>{course.description}</p>
              </div>
            )}

            {activeTab === 'curriculum' && (
              <div>
                <h2 style={{ marginBottom: '1rem' }}>Course Content</h2>
                {course.modules.map(module => (
                  <div key={module.id} style={{ marginBottom: '1.5rem' }}>
                    <h3 style={{ marginBottom: '0.5rem' }}>{module.title}</h3>
                    <div style={{ 
                      backgroundColor: 'var(--light-gray)',
                      borderRadius: '0.5rem',
                      overflow: 'hidden'
                    }}>
                      {module.lessons.map(lesson => (
                        <div key={lesson.id} style={{
                          padding: '1rem',
                          borderBottom: '1px solid var(--border-color)',
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center'
                        }}>
                          <span>{lesson.title}</span>
                          <span style={{ color: '#666' }}>{lesson.duration}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}

            {activeTab === 'reviews' && (
              <div>
                <h2 style={{ marginBottom: '1rem' }}>Student Reviews</h2>
                <p>Reviews coming soon...</p>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div>
            <div className="card" style={{ padding: '2rem', position: 'sticky', top: '2rem' }}>
              <div style={{ height: '200px', backgroundColor: '#eee', marginBottom: '1rem' }}></div>
              <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
                <span style={{ fontSize: '2rem', fontWeight: 'bold' }}>${course.price}</span>
              </div>
              <button
                onClick={handleEnroll}
                className="btn btn-primary"
                style={{ width: '100%', marginBottom: '1rem' }}
              >
                {account ? 'Enroll Now' : 'Sign in to Enroll'}
              </button>
              <div style={{ borderTop: '1px solid var(--border-color)', paddingTop: '1rem' }}>
                <h3 style={{ marginBottom: '1rem' }}>This Course Includes:</h3>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {[
                    '12 hours on-demand video',
                    '15 articles',
                    '10 downloadable resources',
                    'Full lifetime access',
                    'Certificate of completion'
                  ].map((item, index) => (
                    <li key={index} style={{ marginBottom: '0.5rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                      <span>✓</span> {item}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
