import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAlgorand } from '../contexts/AlgorandContext';
import jsPDF from 'jspdf';

interface EnrolledCourse {
  id: string;
  title: string;
  progress: number;
  lastAccessed: string;
  nextLesson: string;
}

interface Certificate {
  courseId: string;
  courseTitle: string;
  userName: string;
  date: string;
}

const Dashboard: React.FC = () => {
  const { account } = useAlgorand();
  const [activeTab, setActiveTab] = useState('my-courses');
  const [certificates, setCertificates] = useState<Certificate[]>([]);

  // Mock data - use YouTube IDs to match CourseDetail
  const enrolledCourses: EnrolledCourse[] = [
    {
      id: 'V6Yqr7prI2I',
      title: 'Algorand Blockchain Tutorial',
      progress: 100, // completed for demo
      lastAccessed: '2 days ago',
      nextLesson: 'Consensus Mechanism'
    },
    {
      id: 'fLAfa-BQtOQ',
      title: 'Blockchain Basics',
      progress: 30,
      lastAccessed: '1 day ago',
      nextLesson: 'Distributed Ledgers'
    }
  ];

  useEffect(() => {
    const certs = JSON.parse(localStorage.getItem('certificates') || '[]');
    setCertificates(certs);
  }, [activeTab]);

  const handleDownloadCertificate = (cert: Certificate) => {
    const doc = new jsPDF();
    doc.setFontSize(22);
    doc.text('Certificate of Completion', 20, 30);
    doc.setFontSize(16);
    doc.text(`This certifies that`, 20, 50);
    doc.setFontSize(18);
    doc.text(cert.userName, 20, 65);
    doc.setFontSize(16);
    doc.text(`has successfully completed the course:`, 20, 80);
    doc.setFontSize(18);
    doc.text(cert.courseTitle, 20, 95);
    doc.setFontSize(14);
    doc.text(`Date: ${cert.date}`, 20, 115);
    doc.save(`${cert.courseTitle}-certificate.pdf`);
  };

  if (!account) {
    return (
      <div className="section">
        <div className="container">
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Please sign in to view your dashboard</h2>
            <Link to="/signin" className="btn btn-primary" style={{ marginTop: '1rem' }}>
              Sign In
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="section">
      <div className="container">
        <h1 style={{ marginBottom: '2rem' }}>My Dashboard</h1>

        {/* Stats Overview */}
        <div className="grid grid-4" style={{ marginBottom: '3rem' }}>
          {[
            { label: 'Enrolled Courses', value: enrolledCourses.length },
            { label: 'Completed Courses', value: enrolledCourses.filter(c => c.progress === 100).length },
            { label: 'Certificates', value: certificates.length },
            { label: 'Learning Hours', value: '12.5' }
          ].map((stat, index) => (
            <div key={index} className="card" style={{ padding: '1.5rem', textAlign: 'center' }}>
              <h3 style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{stat.value}</h3>
              <p style={{ color: '#666' }}>{stat.label}</p>
            </div>
          ))}
        </div>

        {/* Tabs */}
        <div style={{ 
          display: 'flex', 
          borderBottom: '1px solid var(--border-color)',
          marginBottom: '2rem'
        }}>
          {['my-courses', 'certificates', 'achievements'].map(tab => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              style={{
                padding: '1rem 2rem',
                border: 'none',
                background: 'none',
                cursor: 'pointer',
                borderBottom: activeTab === tab ? '2px solid var(--primary-color)' : 'none',
                color: activeTab === tab ? 'var(--primary-color)' : 'var(--text-color)'
              }}
            >
              {tab.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        {activeTab === 'my-courses' && (
          <div>
            <h2 style={{ marginBottom: '1.5rem' }}>My Courses</h2>
            <div className="grid grid-2">
              {enrolledCourses.map(course => (
                <div key={course.id} className="card">
                  <div style={{ padding: '1.5rem' }}>
                    <h3 style={{ marginBottom: '1rem' }}>{course.title}</h3>
                    <div style={{ marginBottom: '1rem' }}>
                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between',
                        marginBottom: '0.5rem'
                      }}>
                        <span>Progress</span>
                        <span>{course.progress}%</span>
                      </div>
                      <div style={{
                        height: '8px',
                        backgroundColor: 'var(--light-gray)',
                        borderRadius: '4px',
                        overflow: 'hidden'
                      }}>
                        <div style={{
                          width: `${course.progress}%`,
                          height: '100%',
                          backgroundColor: 'var(--primary-color)'
                        }}></div>
                      </div>
                    </div>
                    <div style={{ 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      color: '#666',
                      marginBottom: '1rem'
                    }}>
                      <span>Last accessed: {course.lastAccessed}</span>
                      <span>Next: {course.nextLesson}</span>
                    </div>
                    <Link to={`/courses/${course.id}`} className="btn btn-primary" style={{ width: '100%' }}>
                      Continue Learning
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'certificates' && (
          <div>
            <h2 style={{ marginBottom: '1.5rem' }}>My Certificates</h2>
            {certificates.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '3rem' }}>
                <p>No certificates yet. Complete courses to earn certificates!</p>
              </div>
            ) : (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem', maxWidth: 600, margin: '0 auto' }}>
                {certificates.map((cert, idx) => (
                  <div key={idx} className="card" style={{ padding: '1.5rem', display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
                    <div><b>Course:</b> {cert.courseTitle}</div>
                    <div><b>Name:</b> {cert.userName}</div>
                    <div><b>Date:</b> {cert.date}</div>
                    <button
                      className="btn btn-primary"
                      style={{ marginTop: '1rem' }}
                      onClick={() => handleDownloadCertificate(cert)}
                    >
                      Download Certificate
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'achievements' && (
          <div>
            <h2 style={{ marginBottom: '1.5rem' }}>My Achievements</h2>
            <div style={{ textAlign: 'center', padding: '3rem' }}>
              <p>Start learning to earn achievements!</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
